// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.6.12;
pragma experimental ABIEncoderV2;

interface TokenLike { 
  function transferFrom(address from, address to, uint256 amount) external returns (bool);
  function permit(address owner, address spender, uint value, uint deadline, uint8 v, bytes32 r, bytes32 s) external; 
}

contract ACH {

    struct signedPermit {
        address owner; 
        address spender; 
        uint value; 
        uint deadline; 
        uint8 v; 
        bytes32 r;
        bytes32 s;
    }

    mapping (address => uint256) public nonces;

    bytes32   public DOMAIN_SEPARATOR;
    TokenLike public pai;

    // keccak256("pay(address sender, address receiver, uint amount, uint fee, uint nonce, uint expiry, address relayer, uint8 v, bytes32 r, bytes32 s, signedPermit calldata daiPermit)")
    bytes32 public constant PAY_TYPEHASH = 0xf2faaf63b6c0cff21364eb36e463733c74c857a4297e60aba90f98eb0747e688;
    string  public constant version = "1";
    string  public constant name = "Automated Clearing House";

    constructor(address paiAddress) public {
        uint chainId;
        assembly {
            chainId := chainid()
        }

        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                keccak256('EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)'),
                keccak256(bytes(name)),
                keccak256(bytes(version)),
                chainId,
                address(this)
            )
        );

        pai = TokenLike(paiAddress);
    }

    function digest(
        bytes32 hash, 
        address sender, 
        address receiver, 
        uint amount, 
        uint fee, 
        uint nonce, 
        uint expiry, 
        address relayer 
    ) internal view returns (bytes32) {
        return keccak256(abi.encodePacked("\x19\x01", DOMAIN_SEPARATOR, keccak256(abi.encode(hash, sender, receiver, amount, fee, nonce, expiry, relayer))));
    }

    // Transfer @amount dai to the receiver from the sender
    function payWithSignature(
        address sender, 
        address receiver, 
        uint amount, 
        uint fee, 
        uint nonce,
        uint expiry, 
        address relayer, 
        uint8 v, bytes32 r, bytes32 s
    ) public {
        require(sender != address(0) && 
                sender == ecrecover(digest(PAY_TYPEHASH, sender, receiver, amount, fee, nonce, expiry, relayer), v, r, s), 
                "invalid payment");
        require(nonce  == nonces[sender]++, "invalid nonce");
        require(expiry == 0 || now <= expiry, "payment expired");
        pai.transferFrom(sender, relayer, fee);
        pai.transferFrom(sender, receiver, amount);
    }

    function pay(address sender, address receiver, uint amount, uint fee, uint nonce,
                     uint expiry, address relayer, uint8 v, bytes32 r, bytes32 s,
                     signedPermit calldata daiPermit) external {
    pai.permit(daiPermit.owner, daiPermit.spender, daiPermit.value, daiPermit.deadline, daiPermit.v, daiPermit.r, daiPermit.s);
    payWithSignature(sender, receiver, amount, fee, nonce, expiry, relayer, v, r, s);
  }
}
